  /// Získat ThemeData podle ID
  static ThemeData getTheme(String themeId) {
    final theme = _themes[themeId];
    if (theme == null) {
      throw ArgumentError('Theme "$themeId" neexistuje v registru');
    }
    return theme;
  }

  /// Získat metadata podle ID
  static ThemeMetadata getMetadata(String themeId) {
    final metadata = _metadata[themeId];
    if (metadata == null) {
      throw ArgumentError('Metadata pro theme "$themeId" neexistuje');
    }
    return metadata;
  }

  /// Zkontrolovat zda theme ID existuje
  static bool isValidThemeId(String themeId) {
    return _themes.containsKey(themeId);
  }

  /// Získat všechny dostupné theme IDs
  static List<String> getAllThemeIds() {
    return _themes.keys.toList();
  }

  /// Získat všechna metadata (pro UI seznam)
  static List<ThemeMetadata> getAllMetadata() {
    return _metadata.values.toList();
  }

  /// Fallback theme (pokud requested theme neexistuje)
  static ThemeData get defaultTheme => _themes['doom_one']!;
}
